<!-- contenna --><script type="text/javascript">	function add_modul(){		var param_url='<?php echo site_url();?>/user_privilege/add_kopeten/';				  $.ajax({			type: 'POST',			url :  param_url,			data: $("#privilege_kopeten").serialize(),			success: function(data) {				$('#form_add').html(data);			}		});	}	function hapus_modul(user_group_menu_id){		var param_url='<?php echo site_url();?>';		var id = $('#USER_GROUP_ID').val();		var r=confirm("Hapus ? ");		if (r==true){			$.ajax({				type: 'POST',				url :  param_url + '/user_privilege/hapus_modul_kopeten/' + user_group_menu_id ,				data: $("#privilege_kopeten").serialize(),				success: function(data) {					/*$('#content_form').html(data); */					$('#list_data_tab4').load('user_privilege/load_edit_kopeten/' + id); 				}			});		}			}</script>	<li>			<a href="javascript:void(0);" onClick="add_modul();" class="control">&nbsp; Tambah Akses Modul &nbsp; </a>	</li>	<div id="form_add"></div>	<?=form_open('user_privilege/proses_edit_kopeten', array('class'=>'sform','name'=>'privilege_kopeten','id'=>'privilege_kopeten'))?>	<fieldset>		<?php 			if(validation_errors())			{		?>				<ul class="message error grid_12">					<li><?=validation_errors()?></li>					<li class="close-bt"></li>				</ul>			<?php			}									$MENU = array();		?>		<ol>			<li>				<label for="">User Group Admin : <?php echo  isset($results->row()->USER_GROUP_NAME)?$results->row()->USER_GROUP_NAME:$results_by->row()->USER_GROUP_NAME; ?>  <em>*</em></label>							</li>			<hr/>			<input type="hidden" name="ID" value="<?=$id?>">			<input type="hidden" id="USER_GROUP_ID" name="USER_GROUP_ID" value="<?=$results_by->row()->USER_GROUP_ID;?>">		<?php $i=0; foreach($results->result() as $row) { $i++;?>															<?php 						echo "<input type='hidden' name='MENU[$i][MENU_ID]' value=" . $row->MENU_ID . ">";						echo "<input type='hidden' name='MENU[$i][USER_GROUP_MENU_ID]' value=" . $row->USER_GROUP_MENU_ID . ">";					?>										<li><label for="">MENU  <?php echo $row->MENU_NAME ?> <em>*</em></label>						Menu <input name="<?="MENU[$i][HAKAKSES0]";?>" value="<?=$row->PRIVILEGE[0]?>" type="checkbox" <?=$row->PRIVILEGE[0]=='1'?'checked':''?> />						- View <input name="<?="MENU[$i][HAKAKSES1]";?>" value="<?=$row->PRIVILEGE[1]?>" type="checkbox" <?=$row->PRIVILEGE[1]=='1'?'checked':''?> />						- Add <input name="<?="MENU[$i][HAKAKSES2]";?>" value="<?=$row->PRIVILEGE[2]?>" type="checkbox" <?=$row->PRIVILEGE[2]=='1'?'checked':''?> />						- Edit <input name="<?="MENU[$i][HAKAKSES3]";?>" value="<?=$row->PRIVILEGE[3]?>" type="checkbox" <?=$row->PRIVILEGE[3]=='1'?'checked':''?> />						- Delete <input name="<?="MENU[$i][HAKAKSES4]";?>" value="<?=$row->PRIVILEGE[4]?>" type="checkbox" <?=$row->PRIVILEGE[4]=='1'?'checked':''?> />						<a href="javascript:void(0);" onClick="hapus_modul(<?=$row->USER_GROUP_MENU_ID?>);" class="control"><span class="delete">hapus</span></a>					</li>														<hr/>											<?php } ?>				<li>			<!--<input class="greenbutton" type="submit" value="SUBMIT" style="float:right"/> -->			<a href="javascript:void(0);" onclick="edit_privilege_kopeten()" class="control">SUBMIT</a>			<a href="javascript:void(0);" onclick="$('#list_data_tab4').load('user_privilege/get_all_kopeten');" class="control">BACK</a>		</li>		<div class="clearfix">&nbsp;</div>				</ol>	</fieldset>	<?=form_close()?>	