<script type="text/javascript">	var url='<?php echo site_url();?>/users/';	$(document).ready(function() {		var induk_upt = $("#INDUK_UPT").val();		var var_level_id = $("#var_level_id").val();		if (var_level_id=='3'){			$.ajax({				 type:'post',				 url:url + 'get_upt',				 data:'induk_upt=' +induk_upt,				 success:function(data){					   $("#tampil_upt").html(data);				 }			 });    		}			$("#INDUK_UPT").change(function() {									if (var_level_id=='3'){				$.ajax({					 type:'post',					 url:url + 'get_upt',					 data:'induk_upt=' +induk_upt,					 success:function(data){						   $("#tampil_upt").html(data);					 }				 });    			}		});				});</script><input type="hidden" id="var_level_id" name="var_level_id" value="<?=$level_id?>"><label for="">INDUK UPT </label><select id="INDUK_UPT" name="INDUK_UPT"><?php 	$opti['value'] = $kode_upt;	echo $this->users->get_group_diklat_mst_indukupt($opti);?></select><!--	<select name="INDUK_UPT" id="INDUK_UPT">				<option value="0">Pilihan Kosong</option>			<?php 	/*	foreach($results->result()  as $r){			echo '<option value="'.$r->KODE_INDUK.'">'.$r->NAMA_INDUK.'</option>';	} 		*/?>	</select> -->