<script type="text/javascript">	var url='<?php echo site_url();?>/users/';	$(document).ready(function() {	//Default Action		$(".tab_content").hide(); //Hide all content		$("ul.tabs li:first").addClass("active").show(); //Activate first tab		$(".tab_content:first").show(); //Show first tab content				//On Click Event		$("ul.tabs li").click(function() {			$("ul.tabs li").removeClass("active"); //Remove any "active" class			$(this).addClass("active"); //Add "active" class to selected tab			$(".tab_content").hide(); //Hide all tab content			var activeTab = $(this).find("a").attr("href"); //Find the rel attribute value to identify the active tab + content			$(activeTab).fadeIn(); //Fade in the active content			return false;		});	});		$(document).ready(function() {		param_url='<?php echo site_url();?>/users/getall/';		$('#list_data_tab1').load(param_url);				$(function (){			$('#txt_search').change(function (){			  var txt_search = $("#txt_search").val();			  param_url='<?php echo site_url();?>/users/proses_pencarian/';			  $.ajax({					type: 'POST',					url :  param_url,					data: 'txt_search=' + txt_search,					success: function(data) {						$('#list_data_tab1').html(data);					}				});			});		});			});		function get_user_bboard(){		param_url='<?php echo site_url();?>/users/getall';		$('#list_data_tab1').load(param_url);	}	function get_user_sdm(){		param_url='<?php echo site_url();?>/users/get_users_sdm';		$('#list_data_tab2').load(param_url);	}		function ajaxpaging(param_url){		var txt_search=document.getElementById('txt_search').value;		if (param_url== null ){			param_url='<?php echo site_url();?>/users/proses_pencarian/';		}		$.ajax({				type: 'POST',				url :  param_url,				data: 'txt_search=' + txt_search,				success: function(data) {					$('#list_data_tab1').html(data);				}			});	}		function ajaxpaging_sdm(param_url){		var txt_search=document.getElementById('txt_search_sdm').value;		if (param_url== null ){			param_url='<?php echo site_url();?>/users/get_users_sdm/';		}		$.ajax({				type: 'POST',				url :  param_url,				data: 'txt_search=' + txt_search,				success: function(data) {					$('#list_data_tab2').html(data);				}			});	}		function tambah_data(){		/*$.ajax({				type: 'POST',				url :  url + '/add',				data: $("#form_add_users").serialize(),				success: function(data) {					$('#list_data_tab1').html(data);				}			});*/			$('#list_data_tab1').load(url+'add');	}		function add_users(){		$.ajax({			type:'POST',			url :  url + 'proses_add',			data: $("#form_add_users").serialize(),			success: function(data) {				$('#list_data_tab1').html(data);			}		});		return false;	}		function edit_users(id_users){		$('#list_data_tab1').load(url+'edit/' + id_users);	}		function proses_edit(){		$.ajax({			type:'POST',			url :  url + 'proses_edit',			data: $("#form_edit_users").serialize(),			success: function(data) {				$('#list_data_tab1').html(data);			}		});		return false;	}		function add_users_sdm(){		$.ajax({			type:'POST',			url :  url + 'proses_add_sdm',			data: $("#form_add_sdm").serialize(),			success: function(data) {				$('#list_data_tab2').html(data);			}		});		return false;	}	</script><div class="wrap_right bgcontent">	<ul class="tabs">        <li><a href="#tab1" onclick="get_user_bboard()">Buletin Board</a></li>        <li><a href="#tab2" onclick="get_user_sdm()">SDM</a></li>        <li><a href="#tab3">Diklat</a></li>        <li><a href="#tab4">Manajemen Kompetensi</a></li>    </ul>    <div class="tabbed">        <div id="tab1" class="tab_content">            <h2>Buletin Board</h2>            <div id="list_data_tab1"></div>        </div>        <div id="tab2" class="tab_content">            <h2>SDM</h2>            <div id="list_data_tab2"></div>        </div>        <div id="tab3" class="tab_content">            <h2>Diklat</h2>            <div id="list_data_tab3"></div>        </div>    </div>	<div class="clear">&nbsp;</div></div>