<!-- contenna --><script type="text/javascript">	$(document).ready(function() {		var txt_id=document.getElementById('txt_id').value;		param_url='<?php echo site_url();?>/user_privilege/load_edit/' + txt_id;		  $.ajax({				type: 'POST',				url :  param_url,				success: function(data) {					$('#content_form').html(data);				}			});	});	function add_modul(){		var param_url='<?php echo site_url();?>/user_privilege/add/';				  $.ajax({			type: 'POST',			url :  param_url,			data: $("#user_privilege").serialize(),			success: function(data) {				$('#form_add').html(data);			}		});	}		function load_edit_diklat(){		var txt_id=document.getElementById('txt_id').value;		param_url='<?php echo site_url();?>/user_privilege/load_edit_diklat/' + txt_id;		  $.ajax({				type: 'POST',				url :  param_url,				success: function(data) {					$('#content_form').html(data);				}			});	}		function load_edit_sdm(){		var txt_id=document.getElementById('txt_id').value;		param_url='<?php echo site_url();?>/user_privilege/load_edit_sdm/' + txt_id;		  $.ajax({				type: 'POST',				url :  param_url,				success: function(data) {					$('#content_form').html(data);				}			});	}	</script><div class="wrap_right bgcontent" id="edit_privilege">	<h1 class="heading">Data User Privilege</h1>	<li>		<label for="">User Group Diklat : <a href="#" class="control" onclick="load_edit_diklat()">DIKLAT</a></label> - &nbsp; &nbsp; 		<label for="">User Group SDM : <a href="#" class="control" onclick="load_edit_sdm()">SDM</a></label> - &nbsp; &nbsp; 		<label for="">User Group Kompetensi : <a href="#" class="control" onclick="load_edit_kompetensi()">Kompetensi</a></label>  	</li>	<hr/>	<input type="hidden" value="<?=$id?>" name="txt_id" id="txt_id">	<div id="content_form"></div></div><!-- end wrap right content-->