<!-- contenna --><script type="text/javascript">	function add_modul(){		var param_url='<?php echo site_url();?>/user_privilege/add_sdm/';				  $.ajax({			type: 'POST',			url :  param_url,			data: $("#privilege_sdm").serialize(),			success: function(data) {				$('#form_add').html(data);			}		});	}	function hapus_modul(user_group_menu_id){		var param_url='<?php echo site_url();?>';		var r=confirm("Hapus ? ");		if (r==true){			$.ajax({				type: 'POST',				url :  param_url + '/user_privilege/hapus_modul_sdm/' + user_group_menu_id ,				data: $("#user_privilege").serialize(),				success: function(data) {					$('#content_form').html(data);				}			});		}			}</script>	<li>			<a href="javascript:void(0);" onClick="add_modul();" class="control">&nbsp; Tambah Akses Modul &nbsp; </a>	</li>	<div id="form_add"></div>	<?=form_open('user_privilege/proses_edit_sdm', array('class'=>'sform','name'=>'privilege_sdm','id'=>'privilege_sdm'))?>	<fieldset>		<?php 			if(validation_errors())			{		?>				<ul class="message error grid_12">					<li><?=validation_errors()?></li>					<li class="close-bt"></li>				</ul>			<?php			}									$MENU = array();		?>		<ol>			<li>				<label for="">User Group Admin : <?php echo  $results->row()->USER_GROUP_NAME ?> <em>*</em></label>							</li>			<hr/>		<?php $i=0; foreach($results->result() as $row) { $i++;?>										<input type="hidden" name="ID" value="<?=$id?>">					<input type="hidden" name="USER_GROUP_ID" value="<?=$row->USER_GROUP_ID;?>">					<?php 						echo "<input type='hidden' name='MENU[$i][MENU_ID]' value=" . $row->MENU_ID . ">";						echo "<input type='hidden' name='MENU[$i][USER_GROUP_MENU_ID]' value=" . $row->USER_GROUP_MENU_ID . ">";					?>										<li><label for="">MENU  <?php echo $row->MENU_NAME ?> <em>*</em></label>						Menu <input name="<?="MENU[$i][HAKAKSES0]";?>" value="<?=$row->PRIVILEGE[0]?>" type="checkbox" <?=$row->PRIVILEGE[0]=='1'?'checked':''?> />						- View <input name="<?="MENU[$i][HAKAKSES1]";?>" value="<?=$row->PRIVILEGE[1]?>" type="checkbox" <?=$row->PRIVILEGE[1]=='1'?'checked':''?> />						- Add <input name="<?="MENU[$i][HAKAKSES2]";?>" value="<?=$row->PRIVILEGE[2]?>" type="checkbox" <?=$row->PRIVILEGE[2]=='1'?'checked':''?> />						- Edit <input name="<?="MENU[$i][HAKAKSES3]";?>" value="<?=$row->PRIVILEGE[3]?>" type="checkbox" <?=$row->PRIVILEGE[3]=='1'?'checked':''?> />						- Delete <input name="<?="MENU[$i][HAKAKSES4]";?>" value="<?=$row->PRIVILEGE[4]?>" type="checkbox" <?=$row->PRIVILEGE[4]=='1'?'checked':''?> />						<a href="#" onClick="hapus_modul(<?=$row->USER_GROUP_MENU_ID?>);" class="control"><span class="delete">hapus</span></a>					</li>															<hr/>											<?php } ?>				<li><input class="greenbutton" type="submit" value="SUBMIT" style="float:right"/></li>		<div class="clearfix">&nbsp;</div>				</ol>	</fieldset>	<?=form_close()?>	