<script type="text/javascript">	var url='<?php echo site_url();?>/users/';	$(document).ready(function() {				var var_kodeprovin = $("#KODEPROVIN").val();		var var_kodekabup = $("#VAR_KODEKABUP").val();		var var_level_id = $("#VAR_LEVEL_ID").val();		var data = { 'level_id': var_level_id , 'kodeprovin': var_kodeprovin, 'kodekabup': var_kodekabup};				if (var_level_id=='3'){				$.ajax({					 type:'post',					 url:url + 'get_kabupaten',					 data: data,					 success:function(data){						   $("#tampil_kabupaten").html(data);					 }				 });  			}			$("#KODEPROVIN").change(function() {			var kodeprovin = $("#KODEPROVIN").val();			var var_level_id = $("#VAR_LEVEL_ID").val();						if (var_level_id=='3'){				$.ajax({					 type:'post',					 url:url + 'get_kabupaten',					 data:'kodeprovin='+kodeprovin,					 success:function(data){						   $("#tampil_kabupaten").html(data);					 }				 });  			}		});	});</script><input type="hidden" id="VAR_LEVEL_ID" name="VAR_LEVEL_ID" value="<?=$level_id?>"><input type="hidden" id="VAR_KODEKABUP" name="VAR_KODEKABUP" value="<?=$kodekabup?>"><input type="hidden" id="VAR_KODEPROVIN" name="VAR_KODEPROVIN" value="<?=$kodeprovin?>"><li><label for="">PROVINSI <em>*</em></label>				<select id="KODEPROVIN" name="KODEPROVIN">				<?php 					$opti['value'] = $kodeprovin;					echo $this->users->get_group_sdm_provinsi($opti);				?>				</select>			</li><!--<label for="">PROVINSI </label>	<select name="KODEPROVIN" id="KODEPROVIN">				<option value="0">Pilihan Kosong</option>			<?php 	/*	foreach($results->result()  as $r){			echo '<option value="'.$r->KODEPROVIN.'">'.$r->NAMAPROVIN.'</option>';	}		*/?>	</select> -->